import React, { useState } from "react";

function AddGoals() {
  const [content, setContent] = useState("");
  const [list, setList] = useState([]);
 
  return (
    <div style={{ display: "grid", justifyItems: "center" }}>
      <h3 className=" mt-5">Enter the start and end date of challenge</h3>
      <div className="d-flex align-items-center">
        <label for="birthday">Start Date:</label>
        <input
          style={{ width: "150px", margin: "20px", cursor: "pointer" }}
          type="date"
          className="form-control "
          placeholder="start date"
          // value={feet}
          // onChange={userEnterFeet}
        />
        <label for="birthday">End Date:</label>
        <input
          style={{ width: "150px", margin: "20px", cursor: "pointer" }}
          type="date"
          className="form-control border-orange-600"
          placeholder="end date"
          // value={feet}
          // onChange={userEnterFeet}
        />
      </div>
      <div
        style={{
          width: "500px",
          height: "200px",
          backgroundColor: "white",
          margin: "50px",
        }}
      >
        <div
          style={{ display: "grid", justifyContent: "center", padding: "5px" }}
        >
          <h5>Add Monthy Goal</h5>
          <div className="d-flex ">
            <input
              style={{ width: "200px", cursor: "pointer" }}
              type="text"
              className="form-control "
              placeholder="enter your goal....."
              value={content}
              onChange={(e) => {
                setContent(e.target.value);
              }}
            />
            <button
              onClick={() => {
                l.push(content);
                setList(l);
              }}
            >
              ADD GOAL
            </button>
          </div>
          {list &&
            list.map((item) => {
              return <div>{item}</div>;
            })}
        </div>
      </div>
    </div>
  );
}

export default AddGoals;
