import React, { useState, useEffect } from "react";

function MyCalendar() {
  const [currMonth, setCurrMonth] = useState("");
  const [currYear, setCurrYear] = useState("");
  const [prevDates, setPrevDates] = useState([]);
  const [currDates, setCurrDates] = useState([]);
  const [nextDates, setNextDates] = useState([]);

  useEffect(() => {
    update();
  }, []);

  function update() {
    const currentDate = new Date();
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();

    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];

    const day = document.querySelector(".calendar-dates");

    const firstDayOfMonth = new Date(year, month, 1).getDay();
    const lastDateOfMonth = new Date(year, month + 1, 0).getDate();
    const lastDayOfMonth = new Date(year, month, lastDateOfMonth).getDay();
    const lastDateOfPreviousMonth = new Date(year, month, 0).getDate();

    const prevDatesArr = [];
    const currDatesArr = [];
    const nextDatesArr = [];

    for (let i = firstDayOfMonth; i > 0; i--) {
      prevDatesArr.push(lastDateOfPreviousMonth - i + 1);
    }

    for (let i = 1; i <= lastDateOfMonth; i++) {
      currDatesArr.push(i);
    }

    for (let i = lastDayOfMonth; i < 6; i++) {
      nextDatesArr.push(i - lastDayOfMonth + 1);
    }

    setPrevDates(prevDatesArr);
    setCurrDates(currDatesArr);
    setNextDates(nextDatesArr);

    setCurrMonth(months[month]);
    setCurrYear(year);
  }

 function updateNext() {
   let newMonth = month + 1;
   let newYear = year;
   if (newMonth > 11) {
     newYear = year + 1;
     newMonth = 0;
   }
   setDate(new Date(newYear, newMonth, 1));
 }

 function updatePrev() {
   let newMonth = month - 1;
   let newYear = year;
   if (newMonth < 0) {
     newYear = year - 1;
     newMonth = 11;
   }
   setDate(new Date(newYear, newMonth, 1));
 }

  function updateCalendar(year, month) {
    const date = new Date(year, month, 1);
    const newYear = date.getFullYear();
    const newMonth = date.getMonth();

    setCurrYear(newYear);
    setCurrMonth(months[newMonth]);

    update();
  }

  const months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  return (
    <div>
      <h1
        className="d-flex justify-content-center py-4"
        style={{ fontFamily: "Poppins" }}
      >
        ChallengeQuest
      </h1>

      <div className="calendar-container">
        <header className="calendar-header">
          <p className="calendar-current-date">
            <div className="d-flex justify-content-center align-items-center">
              {currMonth} {currYear}
              <div
                style={{
                  borderRadius: "50%",
                  backgroundColor: "green",
                  width: "20px",
                  height: "20px",
                  marginLeft: "200px",
                }}
              ></div>
              <div style={{ fontSize: "1rem" }}>-To check in</div>
              <div
                style={{
                  borderRadius: "50%",
                  backgroundColor: "orange",
                  width: "20px",
                  height: "20px",
                  marginLeft: "10px",
                }}
              ></div>{" "}
              <div style={{ fontSize: "1rem" }}>-To add goals</div>
            </div>
          </p>

          <div className="calendar-navigation">
            <span
              id="calendar-prev"
              className="material-symbols-rounded"
              onClick={updatePrev}
            >
              &lt;
            </span>
            <span
              id="calendar-next"
              className="material-symbols-rounded"
              onClick={updateNext}
            >
              &gt;
            </span>
          </div>
        </header>

        <div className="calendar-body">
          <ul className="calendar-weekdays">
            <li>Sun</li>
            <li>Mon</li>
            <li>Tue</li>
            <li>Wed</li>
            <li>Thu</li>
            <li>Fri</li>
            <li>Sat</li>
          </ul>
          <ul className="calendar-dates">
            {prevDates.map((elem) => (
              <li key={elem} className="inactive">
                {elem}
              </li>
            ))}
            {currDates.map((elem) => (
              <li key={elem} className="active" style={{ lineHeight: "22px" }}>
                {elem}
                <div className="d-flex">
                  <div
                    style={{
                      borderRadius: "50%",
                      backgroundColor: "green",
                      width: "15px",
                      height: "15px",
                      marginLeft: "50px",
                    }}
                  
                  ></div>
                  <div
                    style={{
                      borderRadius: "50%",
                      backgroundColor: "orange",
                      width: "15px",
                      height: "15px",
                      marginLeft: "5px",
                    }}
                  ></div>
                </div>
              </li>
            ))}
            {nextDates.map((elem) => (
              <li key={elem} className="inactive">
                {elem}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default MyCalendar;
