import React from "react";

function EverdayGoal() {
  return (
    <div>
      <div
        style={{
          width: "200px",
          minHeight: "300px",
          backgroundColor: "#FFCEB1",
          zIndex: "2",
          marginLeft: "50px",
        }}
        className="d-flex justify-content-center"
      >
        <h6 className="mt-3">Add Todays Goal</h6>
        <div
          style={{
            width: "500px",
            minHeight: "300px",
            backgroundColor: "white",
            margin: "50px",
            padding: "20px",
          }}
          className="d-flex align-items-center  flex-column"
        >
          <h5 className="">Add Monthy Goal</h5>
          <div className="d-flex ">
            <input
              style={{ width: "300px", cursor: "pointer" }}
              type="text"
              className="form-control "
              placeholder="enter your goal....."
              value={content}
              onChange={(e) => {
                setContent(e.target.value);
              }}
            />
            <button
              onClick={() => {
                if (content) setList((prevList) => [...prevList, content]);
                setContent("");
              }}
            >
              ADD GOAL
            </button>
          </div>
          {list &&
            list.map((item, index) => {
              const handleDelete = () => {
                // Create a copy of the list array
                const updatedList = [...list];
                // Remove the item at the current index
                console.log(index);
                updatedList.splice(index, 1);
                // Update the list state with the new array
                setList(updatedList);
              };

              return (
                <div
                  key={index}
                  style={{
                    width: "400px",
                    position: "relative",
                    margin: "4px",
                  }}
                >
                  {item}
                  <button
                    style={{ position: "absolute", right: "0" }}
                    onClick={handleDelete}
                  >
                    delete
                  </button>
                </div>
              );
            })}
        </div>
      </div>
    </div>
  );
}

export default EverdayGoal;
