import React, { useState, useEffect } from "react";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  getDocs,
  deleteDoc,
  updateDoc,
  doc,
} from "firebase/firestore";
import { startQuest, } from "./FunctionStack";
function Home() {
  const [st, setSt] = useState(""); // Define st as a state variable
  const [startDate, setStartDate] = useState([]);
  const [endDate, setEndDate] = useState();
  const [id, setId] = useState("");
  const b = collection(db, "startdate");

  const startQuest = async () => {
    const data = await getDocs(b);
    const filteredData = data.docs.map((doc) => ({
      ...doc.data(),
      id: doc.id,
    }));

    setStartDate(filteredData);
  };

  const setQuest = async (data) => {
    if (data && startDate.length === 0) {
      await addDoc(b, {
        startQuest: data,
      });
      startQuest();
    }
  };

  const update = async (x, newValue) => {
    // Pass newValue as an argument
    const z = doc(db, "startdate", x);
    await updateDoc(z, { startQuest: newValue }); // Use newValue instead of st
    startQuest();
  };

  useEffect(() => {
    startQuest();
  }, []);

  const toogle = () => {
    startDate.length === 0 ? setQuest(st) : update(id, st); // Pass st as an argument to update
  };

  return (
    <>
      <div className="d-flex justify-content-center align-items-center flex-column vh-100">
        <h1>ChallengeQuest</h1>
        <p className="px-5 text-center">
          Welcome to our challenge quest website, where you embark on exciting
          journeys towards your goals! Are you ready to take on a challenge and
          push your limits? Whether you're aiming to improve your fitness,
          develop new habits, or conquer personal milestones, our platform is
          here to support you every step of the way.
        </p>
        <p className="px-5 text-center">
          To begin your adventure, simply set the start date and end date of
          your challenge. Whether you're looking for a short-term sprint or a
          long-term commitment, our customizable challenges allow you to tailor
          your journey to fit your schedule and preferences.
        </p>

        <div className="d-flex justify-content-center align-items-center">
          <label htmlFor="birthday">Start Date:</label>
          <input
            style={{ width: "150px", margin: "20px", cursor: "pointer" }}
            type="date"
            className="form-control "
            placeholder="start date"
            onChange={(e) => {
              setSt(e.target.value); // Update st using setSt
            }}
            value={st}
          />

          <button onClick={toogle}>
            {startDate.length === 0 ? "set" : "edit"}
          </button>
          <label>End Date:</label>
          <input
            style={{ width: "150px", margin: "20px", cursor: "pointer" }}
            type="date"
            className="form-control border-orange-600"
            placeholder="end date"
          />
          {startDate &&
            startDate.map((item) => {
              if (!id) setId(item.id);
              return <div key={item.id}>{item.startQuest}</div>;
            })}
        </div>
      </div>
    </>
  );
}

export default Home;
