import React, { useEffect, useState } from "react";
import { db } from "./firebase";
import { collection, addDoc, getDocs } from "firebase/firestore";

function AddGoals() {
  const [content, setContent] = useState("");
  const [list, setList] = useState([]);
  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();
  const a = collection(db, "challenger");
  useEffect(() => {
    
    getGoals();
  }, []);
  const onSubmitContent = async () => {
    await addDoc(a, {
      content: { content },
    });
  };
  return (
    <div style={{ display: "grid", justifyItems: "center" }}>
      <h3 className=" mt-5">Enter the start and end date of challenge</h3>
      <div className="d-flex align-items-center">
        <label for="birthday">Start Date:</label>
        <input
          style={{ width: "150px", margin: "20px", cursor: "pointer" }}
          type="date"
          className="form-control "
          placeholder="start date"
          onChange={(e) => {
            setStartDate(e.target.value);
            console.log(startDate);
          }}
          // value={feet}
          // onChange={userEnterFeet}
        />
        <label>End Date:</label>
        <input
          style={{ width: "150px", margin: "20px", cursor: "pointer" }}
          type="date"
          className="form-control border-orange-600"
          placeholder="end date"
          // value={feet}
          // onChange={userEnterFeet}
        />
      </div>
      <div
        style={{
          width: "500px",
          minHeight: "300px",
          backgroundColor: "white",
          margin: "50px",
          padding: "20px",
        }}
        className="d-flex align-items-center  flex-column"
      >
        <h5 className="">Add Monthy Goal</h5>
        <div className="d-flex ">
          <input
            style={{ width: "300px", cursor: "pointer" }}
            type="text"
            className="form-control "
            placeholder="enter your goal....."
            value={content}
            onChange={(e) => {
              setContent(e.target.value);
            }}
          />
          <button
            onClick={() => {
              if (content) setList((prevList) => [...prevList, content]);
              setContent("");
            }}
          >
            ADD GOAL
          </button>
        </div>
        {list &&
          list.map((item, index) => {
            const handleDelete = () => {
              // Create a copy of the list array
              const updatedList = [...list];
              // Remove the item at the current index
              console.log(index);
              updatedList.splice(index, 1);
              // Update the list state with the new array
              setList(updatedList);
            };

            return (
              <div
                key={index}
                style={{
                  width: "400px",
                  position: "relative",
                  margin: "4px",
                }}
              >
                {item}
                <button
                  style={{ position: "absolute", right: "0" }}
                  onClick={handleDelete}
                >
                  delete
                </button>
              </div>
            );
          })}
      </div>
    </div>
  );
}

export default AddGoals;
