export const startQuest = async () => {
  const data = await getDocs(b);
  const filteredData = data.docs.map((doc) => ({
    ...doc.data(),
    id: doc.id,
  }));

  setStartDate(filteredData);
};

export const setQuest = async (data) => {
  if (data && startDate.length === 0) {
    await addDoc(b, {
      startQuest: data,
    });
    startQuest();
  }
};

export const update = async (x, newValue) => {
  // Pass newValue as an argument
  const z = doc(db, "startdate", x);
  await updateDoc(z, { startQuest: newValue }); // Use newValue instead of st
  startQuest();
};
