import React, { useState, useEffect } from "react";

function MyCalendar() {
  const [currMonth, setCurrMonth] = useState("");
  const [currYear, setCurrYear] = useState("");
  const [prevDates, setPrevDates] = useState([]);
  const [currDates, setCurrDates] = useState([]);
  const [nextDates, setNextDates] = useState([]);
  const [date, setDate] = useState();
  const [year, setYear] = useState();
  const [month, setMonth] = useState();

  useEffect(() => {
    update();
  }, []);

  const update = () => {
    const currentDate = new Date();
    setDate(currentDate);
    setYear(currentDate.getFullYear());
    setMonth(currentDate.getMonth());
    manipulate();
  };

  const manipulate = () => {
    const day = document.querySelector(".calendar-dates");
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];

    let dayone = new Date(year, month, 1).getDay();
    let lastdate = new Date(year, month + 1, 0).getDate();
    let dayend = new Date(year, month, lastdate).getDay();
    let monthlastdate = new Date(year, month, 0).getDate();

    let lit = "";

    let newElem = [];
    for (let i = dayone; i > 0; i--) {
      newElem.push(monthlastdate - i + 1);
    }
    setPrevDates(newElem);
    newElem = [];

    for (let i = 1; i <= lastdate; i++) {
      newElem.push(i);
    }
    setCurrDates(newElem);
    newElem = [];

    for (let i = dayend; i < 6; i++) {
      newElem.push(i - dayend + 1);
    }
    setNextDates(newElem);

    setCurrMonth(months[month]);
    setCurrYear(year);

    day.innerHTML = lit;
  };

  const handleIconClick = (direction) => {
    setMonth((prevMonth) =>
      direction === "prev" ? prevMonth - 1 : prevMonth + 1
    );
  };
  ma

  return (
    <div>
      <h1
        className="d-flex justify-content-center py-4"
        style={{ fontFamily: "Poppins" }}
      >
        ChallengeQuest
      </h1>

      <div className="calendar-container">
        <header className="calendar-header">
          <p className="calendar-current-date">
            <div className="d-flex justify-content-center align-items-center">
              {currMonth} {currYear}
              <div
                style={{
                  borderRadius: "50%",
                  backgroundColor: "green",
                  width: "20px",
                  height: "20px",
                  marginLeft: "200px",
                }}
              ></div>
              <div style={{ fontSize: "1rem" }}>-To check in</div>
              <div
                style={{
                  borderRadius: "50%",
                  backgroundColor: "orange",
                  width: "20px",
                  height: "20px",
                  marginLeft: "10px",
                }}
              ></div>{" "}
              <div style={{ fontSize: "1rem" }}>-To add goals</div>
            </div>
          </p>

          <div className="calendar-navigation">
            <span
              id="calendar-prev"
              className="material-symbols-rounded"
              onClick={() => handleIconClick("prev")}
            >
              &lt;
            </span>
            <span
              id="calendar-next"
              className="material-symbols-rounded"
              onClick={() => handleIconClick("next")}
            >
              &gt;
            </span>
          </div>
        </header>

        <div className="calendar-body">
          <ul className="calendar-weekdays">
            <li>Sun</li>
            <li>Mon</li>
            <li>Tue</li>
            <li>Wed</li>
            <li>Thu</li>
            <li>Fri</li>
            <li>Sat</li>
          </ul>
          <ul className="calendar-dates">
            {prevDates.map((elem, index) => (
              <li key={index} className="inactive">
                {elem}
              </li>
            ))}
            {currDates.map((elem, index) => {
              let today = new Date();
              let currentYear = today.getFullYear();
              let currentMonth = today.getMonth();
              let isToday =
                elem === today.getDate() &&
                month === currentMonth &&
                year === currentYear
                  ? "active"
                  : "";
              return (
                <li key={index} className={isToday}>
                  {elem}
                </li>
              );
            })}
            {nextDates.map((elem, index) => (
              <li key={index} className="inactive">
                {elem}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default MyCalendar;
