import React, { useState, useEffect } from "react";

function MyCalendar() {
  const [currMonth, setCurrMonth] = useState("");
  const [currYear, setCurrYear] = useState("");
  const [prevDates, setPrevDates] = useState([]);
  const [currDates, setCurrDates] = useState([]);
  const [nextDates, setNextDates] = useState([]);
  var [date, setDate] = useState();
  var [year, setYear] = useState();
  var [month, setMonth] = useState();
  const [color, setColor] = useState("yellow");
  function update() {
    // const currentDate1 = new Date();
    // setDate(currentDate1);
    // setYear(currentDate1.getFullYear());
    // setMonth(currentDate1.getMonth());

    // setDate(new Date());
    // setYear(date.getFullYear());
    // setMonth(date.getMonth());
    const day = document.querySelector(".calendar-dates");

    const prenexIcons = document.querySelectorAll(".calendar-navigation span");

    // Array of month names
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];

    // Function to generate the calendar
    const manipulate = () => {
      // Get the first day of the month
      let dayone = new Date(year, month, 1).getDay();

      // Get the last date of the month
      let lastdate = new Date(year, month + 1, 0).getDate();
      console.log(lastdate);
      // Get the day of the last date of the month
      let dayend = new Date(year, month, lastdate).getDay();

      // Get the last date of the previous month
      let monthlastdate = new Date(year, month, 0).getDate();

      // Variable to store the generated calendar HTML
      let lit = "";

      // Loop to add the last dates of the previous month
      console.log(dayone);
      var newElem = [];
      for (let i = dayone; i > 0; i--) {
        newElem.push(monthlastdate - i + 1);
        // lit += `<li class="inactive">${monthlastdate - i + 1}</li>`;
      }
      setPrevDates(newElem);
      newElem = [];

      // Loop to add the dates of the current month
      for (let i = 1; i <= lastdate; i++) {
        // Check if the current date is today

        newElem.push(i);
        // lit += `<li  class="${isToday}">${i}</li>`;
      }
      setCurrDates(newElem);
      newElem = [];
      // Loop to add the first dates of the next month
      for (let i = dayend; i < 6; i++) {
        // lit += `<li class="inactive">${i - dayend + 1}</li>`;
        newElem.push(i - dayend + 1);
      }
      setNextDates(newElem);

      // Update the text of the current date element
      // with the formatted current month and year

      setCurrMonth(months[month]);
      setCurrYear(year);

      // update the HTML of the dates element
      // with the generated calendar
      // day.innerHTML = lit;
    };

    manipulate();

    // Attach a click event listener to each icon
    // prenexIcons.forEach((icon) => {
    //   // When an icon is clicked
    //   icon.addEventListener("click", () => {
    //     // Check if the icon is "calendar-prev"
    //     // or "calendar-next"
    //     month = icon.id === "calendar-prev" ? month - 1 : month + 1;

    //     // Check if the month is out of range
    //     if (month < 0 || month > 11) {
    //       // Set the date to the first day of the
    //       // month with the new year
    //       date = new Date(year, month, new Date().getDate());

    //       // Set the year to the new year
    //       year = date.getFullYear();

    //       // Set the month to the new month
    //       month = date.getMonth();
    //     } else {
    //       // Set the date to the current date
    //       date = new Date();
    //     }

    //     // Call the manipulate function to
    //     // update the calendar display
    //     manipulate();
    //   });
    // });
  }
  useEffect(() => {
    update();
  }, []);
  function updateNext() {
    if (month > 11) {
      setDate(new Date(year, month + 1, new Date().getDate()));

      // Set the year to the new year
      setYear(date.getFullYear());

      // Set the month to the new month
      setMonth(date.getMonth());
    } else {
      // Set the date to the current date
      setDate(new Date());
    }
    update();
  }
  function updatePrev() {
    if (month < 0) {
      setDate(new Date(year, month - 1, new Date().getDate()));

      // Set the year to the new year
      setYear(date.getFullYear());

      // Set the month to the new month
      setMonth(date.getMonth());
    } else {
      // Set the date to the current date
      setDate(new Date());
    }
    update();
  }
  return (
    <div>
      <h1
        className="d-flex justify-content-center py-4  "
        style={{ fontFamily: "Poppins" }}
      >
        ChallengeQuest
      </h1>

      <div class="calendar-container">
        <header class="calendar-header">
          <p class="calendar-current-date">
            <div className="d-flex justify-content-center align-items-center">
              {currMonth} {currYear}
              <div
                style={{
                  borderRadius: "50%",
                  backgroundColor: "green",
                  width: "20px",
                  height: "20px",
                  marginLeft: "200px",
                }}
              ></div>
              <div style={{ fontSize: "1rem" }}>-To check in</div>
              <div
                style={{
                  borderRadius: "50%",
                  backgroundColor: "orange",
                  width: "20px",
                  height: "20px",
                  marginLeft: "10px",
                }}
              ></div>{" "}
              <div style={{ fontSize: "1rem" }}>-To add goals</div>
            </div>
          </p>

          <div class="calendar-navigation">
            <span
              id="calendar-prev"
              class="material-symbols-rounded "
              onClick={updateNext}
            >
              &lt;
            </span>
            <span
              id="calendar-next"
              class="material-symbols-rounded"
              onClick={updatePrev}
            >
              &gt;
            </span>
          </div>
        </header>

        <div class="calendar-body">
          <ul class="calendar-weekdays">
            <li>Sun</li>
            <li>Mon</li>
            <li>Tue</li>
            <li>Wed</li>
            <li>Thu</li>
            <li>Fri</li>
            <li>Sat</li>
          </ul>
          <ul class="calendar-dates">
            {prevDates.map((elem) => (
              <li class="inactive">{elem}</li>
            ))}
            {currDates.map((elem) => {
              let date1 = new Date();
              let year1 = date1.getFullYear();
              let month1 = date1.getMonth();
              let isToday =
                elem === date1.getDate() &&
                month1 === new Date().getMonth() &&
                year1 === new Date().getFullYear()
                  ? "active"
                  : "";
              return (
                <li class={isToday} style={{ lineHeight: "22px" }}>
                  {elem}
                  <div className="d-flex  ">
                    <div
                      style={{
                        borderRadius: "50%",
                        backgroundColor: "green",
                        width: "15px",
                        height: "15px",
                        marginLeft: "50px",
                      }}
                      onClick={() => {
                        setColor("yellow");
                      }}
                    ></div>
                    <div
                      style={{
                        borderRadius: "50%",
                        backgroundColor: "orange",
                        width: "15px",
                        height: "15px",
                        marginLeft: "5px",
                      }}
                    ></div>
                  </div>
                </li>
              );
            })}
            {nextDates.map((elem) => {
              return <li className="inactive">{elem}</li>;
            })}
          </ul>
        </div>
      </div>
    </div>
  );
}

export default MyCalendar;
