import React, { useState } from "react";

function EverdayGoal() {
  const [content, setContent] = useState("");
  const [list, setList] = useState([]);
  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();
  return (
    <div>
      <div className="d-flex justify-content-center">
        <h6 className="mt-3">Add Todays Goal</h6>
      </div>
      <div
        style={{
          width: "200px",
          minHeight: "300px",
          backgroundColor: "#FFCEB1",
          zIndex: "2",
          marginLeft: "50px",
        }}
        className="d-flex align-items-center  flex-column"
      >
        <h5 className="">Add Monthy Goal</h5>
        <div className="d-flex ">
          <input
            style={{ width: "120px", cursor: "pointer" }}
            type="text"
            className="form-control "
            placeholder="enter your goal....."
            value={content}
            onChange={(e) => {
              setContent(e.target.value);
            }}
          />
          <button
            onClick={() => {
              if (content) setList((prevList) => [...prevList, content]);
              setContent("");
            }}
            style={{width}}
          >
            ADD GOAL
          </button>
        </div>
        {list &&
          list.map((item, index) => {
            const handleDelete = () => {
              // Create a copy of the list array
              const updatedList = [...list];
              // Remove the item at the current index
              console.log(index);
              updatedList.splice(index, 1);
              // Update the list state with the new array
              setList(updatedList);
            };

            return (
              <div
                key={index}
                style={{
                  width: "400px",
                  position: "relative",
                  margin: "4px",
                }}
              >
                {item}
                <button
                  style={{ position: "absolute", right: "0" }}
                  onClick={handleDelete}
                >
                  delete
                </button>
              </div>
            );
          })}
      </div>
    </div>
  );
}

export default EverdayGoal;
